import React, { useState, useEffect } from 'react';
import { useNavigate, useParams } from 'react-router-dom';
import { getAlbumById } from '../data/albums';
import './AlbumForm.css';

const AlbumForm = () => {
  const [formData, setFormData] = useState({
    id: '',
    title: '',
    description: '',
    category: 'portrait',
    coverImage: ''
  });
  const [isEditing, setIsEditing] = useState(false);
  const [errors, setErrors] = useState({});
  const navigate = useNavigate();
  const { albumId } = useParams();

  const categories = [
    { value: 'portrait', label: 'Portrait' },
    { value: 'landscape', label: 'Paysage' },
    { value: 'concerts', label: 'Concerts' },
    { value: 'essais', label: 'Essais' },
    { value: 'event', label: 'Événements' }
  ];

  useEffect(() => {
    if (albumId && albumId !== 'new') {
      setIsEditing(true);
      const album = getAlbumById(albumId);
      if (album) {
        setFormData({
          id: album.id,
          title: album.title,
          description: album.description,
          category: album.category,
          coverImage: album.coverImage
        });
      }
    }
  }, [albumId]);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));

    // Clear error when user starts typing
    if (errors[name]) {
      setErrors(prev => ({
        ...prev,
        [name]: ''
      }));
    }
  };

  const generateId = (title) => {
    return title
      .toLowerCase()
      .replace(/[àáâãäå]/g, 'a')
      .replace(/[èéêë]/g, 'e')
      .replace(/[ìíîï]/g, 'i')
      .replace(/[òóôõö]/g, 'o')
      .replace(/[ùúûü]/g, 'u')
      .replace(/[ç]/g, 'c')
      .replace(/[^a-z0-9]/g, '-')
      .replace(/-+/g, '-')
      .replace(/^-|-$/g, '');
  };

  const validateForm = () => {
    const newErrors = {};

    if (!formData.title.trim()) {
      newErrors.title = 'Le titre est requis';
    }

    if (!formData.description.trim()) {
      newErrors.description = 'La description est requise';
    }

    if (!formData.coverImage.trim()) {
      newErrors.coverImage = 'L\'image de couverture est requise';
    } else if (!isValidUrl(formData.coverImage)) {
      newErrors.coverImage = 'L\'URL de l\'image n\'est pas valide';
    }

    if (!isEditing) {
      const generatedId = generateId(formData.title);
      const savedAlbums = JSON.parse(localStorage.getItem('albumsData') || '[]');
      if (savedAlbums.some(album => album.id === generatedId)) {
        newErrors.title = 'Un album avec ce titre existe déjà';
      }
    }

    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  const isValidUrl = (string) => {
    try {
      new URL(string);
      return true;
    } catch (_) {
      return false;
    }
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    if (!validateForm()) {
      return;
    }

    const savedAlbums = JSON.parse(localStorage.getItem('albumsData') || '[]');

    if (isEditing) {
      // Update existing album
      const updatedAlbums = savedAlbums.map(album => 
        album.id === formData.id 
          ? { ...album, ...formData }
          : album
      );
      localStorage.setItem('albumsData', JSON.stringify(updatedAlbums));
    } else {
      // Create new album
      const newAlbum = {
        ...formData,
        id: generateId(formData.title),
        photoCount: 0,
        photos: []
      };

      const updatedAlbums = [...savedAlbums, newAlbum];
      localStorage.setItem('albumsData', JSON.stringify(updatedAlbums));
    }

    navigate('/admin/dashboard');
  };

  return (
    <div className="album-form-page">
      <div className="admin-header">
        <div className="container">
          <div className="header-content">
            <h1>{isEditing ? 'Modifier l\'album' : 'Nouvel album'}</h1>
            <button 
              onClick={() => navigate('/admin/dashboard')} 
              className="back-btn"
            >
              Retour
            </button>
          </div>
        </div>
      </div>

      <div className="form-content">
        <div className="container">
          <div className="form-container">
            <form onSubmit={handleSubmit} className="album-form">
              <div className="form-group">
                <label htmlFor="title">Titre de l'album *</label>
                <input
                  type="text"
                  id="title"
                  name="title"
                  value={formData.title}
                  onChange={handleChange}
                  className={errors.title ? 'error' : ''}
                  placeholder="Entrez le titre de l'album"
                />
                {errors.title && <span className="error-text">{errors.title}</span>}
              </div>

              <div className="form-group">
                <label htmlFor="description">Description *</label>
                <textarea
                  id="description"
                  name="description"
                  value={formData.description}
                  onChange={handleChange}
                  className={errors.description ? 'error' : ''}
                  placeholder="Décrivez l'album"
                  rows="4"
                />
                {errors.description && <span className="error-text">{errors.description}</span>}
              </div>

              <div className="form-group">
                <label htmlFor="category">Catégorie *</label>
                <select
                  id="category"
                  name="category"
                  value={formData.category}
                  onChange={handleChange}
                >
                  {categories.map(cat => (
                    <option key={cat.value} value={cat.value}>
                      {cat.label}
                    </option>
                  ))}
                </select>
              </div>

              <div className="form-group">
                <label htmlFor="coverImage">Image de couverture (URL) *</label>
                <input
                  type="url"
                  id="coverImage"
                  name="coverImage"
                  value={formData.coverImage}
                  onChange={handleChange}
                  className={errors.coverImage ? 'error' : ''}
                  placeholder="https://example.com/image.jpg"
                />
                {errors.coverImage && <span className="error-text">{errors.coverImage}</span>}
                {formData.coverImage && isValidUrl(formData.coverImage) && (
                  <div className="image-preview">
                    <img src={formData.coverImage} alt="Aperçu" />
                  </div>
                )}
              </div>

              {!isEditing && (
                <div className="form-group">
                  <label>ID généré automatiquement</label>
                  <input
                    type="text"
                    value={formData.title ? generateId(formData.title) : ''}
                    disabled
                    className="generated-id"
                  />
                  <small>L'ID est généré automatiquement à partir du titre</small>
                </div>
              )}

              <div className="form-actions">
                <button 
                  type="button" 
                  onClick={() => navigate('/admin/dashboard')}
                  className="cancel-btn"
                >
                  Annuler
                </button>
                <button type="submit" className="submit-btn">
                  {isEditing ? 'Mettre à jour' : 'Créer l\'album'}
                </button>
              </div>
            </form>
          </div>
        </div>
      </div>
    </div>
  );
};

export default AlbumForm;
