// Helper function to publish albums to GitHub via repository dispatch
export const publishToGitHub = async (repoOwner, repoName, githubToken) => {
  try {
    const savedAlbums = JSON.parse(localStorage.getItem('albumsData') || '[]');
    const albumsJSContent = generateCompleteAlbumsJS(savedAlbums);

    // Trigger GitHub Actions workflow via repository dispatch
    const response = await fetch(`https://api.github.com/repos/${repoOwner}/${repoName}/dispatches`, {
      method: 'POST',
      headers: {
        'Authorization': `token ${githubToken}`,
        'Accept': 'application/vnd.github.v3+json',
        'Content-Type': 'application/json'
      },
      body: JSON.stringify({
        event_type: 'update-albums',
        client_payload: {
          albums_data: albumsJSContent,
          updated_by: 'Admin Interface',
          timestamp: new Date().toISOString(),
          albums_count: savedAlbums.length
        }
      })
    });
    if (response.ok) {
      console.log('Albums published to GitHub successfully');
      return {
        success: true,
        message: 'Modifications publiées avec succès ! Le site sera mis à jour dans quelques minutes.'
      };
    } else {
      const errorData = await response.json();
      console.error('Failed to publish to GitHub:', errorData);
      return {
        success: false,
        message: `Erreur GitHub: ${errorData.message || 'Erreur inconnue'}`
      };
    }
  } catch (error) {
    console.error('Error publishing to GitHub:', error);
    return {
      success: false,
      message: 'Erreur de connexion à GitHub. Vérifiez votre token et votre connexion.'
    };
  }
};

// Helper function to save albums data to GitHub (main function)
export const saveAlbumsToGitHub = async () => {
  const token = getGitHubToken();
  if (!token) {
    return {
      success: false,
      message: 'Token GitHub requis. Veuillez configurer votre token d\'accès.',
      requiresToken: true
    };
  }

  // Ces valeurs doivent être configurées selon votre repository
  const owner = GITHUB_CONFIG.owner;
  const repo = GITHUB_CONFIG.repo;
  if (owner === 'VOTRE_USERNAME' || repo === 'VOTRE_REPO') {
    return {
      success: false,
      message: 'Configuration GitHub requise. Veuillez configurer votre repository.',
      requiresConfig: true
    };
  }
  return await publishToGitHub(owner, repo, token);
};

// Helper function to configure GitHub repository
export const configureGitHub = (owner, repo) => {
  GITHUB_CONFIG.owner = owner;
  GITHUB_CONFIG.repo = repo;
  localStorage.setItem('github_config', JSON.stringify({
    owner,
    repo
  }));
};

// Helper function to load GitHub configuration
export const loadGitHubConfig = () => {
  const saved = localStorage.getItem('github_config');
  if (saved) {
    const config = JSON.parse(saved);
    GITHUB_CONFIG.owner = config.owner;
    GITHUB_CONFIG.repo = config.repo;
  }
  return GITHUB_CONFIG;
};

