// Helper function to download the exported albums.js file
export const downloadAlbumsJS = () => {
  const content = exportToAlbumsJS();
  const blob = new Blob([content], {
    type: 'text/javascript'
  });
  const url = URL.createObjectURL(blob);
  const link = document.createElement('a');
  link.href = url;
  link.download = 'albums.js';
  document.body.appendChild(link);
  link.click();
  document.body.removeChild(link);
  URL.revokeObjectURL(url);
};

// Helper function to save albums data to server
export const saveAlbumsToServer = async () => {
  try {
    const savedAlbums = JSON.parse(localStorage.getItem('albumsData') || '[]');
    const response = await fetch('http://localhost:3001/api/albums/save', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json'
      },
      body: JSON.stringify({
        albums: savedAlbums
      })
    });
    const result = await response.json();
    if (result.success) {
      console.log('Albums saved to server successfully');
      return {
        success: true,
        message: 'Données sauvegardées avec succès sur le serveur'
      };
    } else {
      console.error('Failed to save albums to server:', result.error);
      return {
        success: false,
        message: 'Erreur lors de la sauvegarde: ' + result.error
      };
    }
  } catch (error) {
    console.error('Error saving albums to server:', error);
    return {
      success: false,
      message: 'Erreur de connexion au serveur'
    };
  }
};

