      localStorage.setItem('albumsData', JSON.stringify(result.data));
      console.log('Albums loaded from server successfully');
      return {
        success: true,
        data: result.data
      };
    } else {
      console.error('Failed to load albums from server:', result.error);
      return {
        success: false,
        message: 'Erreur lors du chargement: ' + result.error
      };
    }
  } catch (error) {
    console.error('Error loading albums from server:', error);
    return {
      success: false,
      message: 'Erreur de connexion au serveur'
    };
  }
};

