import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import './PhotoForm.css';

const PhotoForm = () => {
  const [formData, setFormData] = useState({
    albumId: '',
    src: '',
    title: '',
    description: ''
  });
  const [albums, setAlbums] = useState([]);
  const [errors, setErrors] = useState({});
  const navigate = useNavigate();

  useEffect(() => {
    // Load albums from localStorage
    const savedAlbums = JSON.parse(localStorage.getItem('albumsData') || '[]');
    setAlbums(savedAlbums);
  }, []);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));

    // Clear error when user starts typing
    if (errors[name]) {
      setErrors(prev => ({
        ...prev,
        [name]: ''
      }));
    }
  };

  const validateForm = () => {
    const newErrors = {};

    if (!formData.albumId) {
      newErrors.albumId = 'Veuillez sÃ©lectionner un album';
    }

    if (!formData.src.trim()) {
      newErrors.src = 'L\'URL de la photo est requise';
    } else if (!isValidUrl(formData.src)) {
      newErrors.src = 'L\'URL de la photo n\'est pas valide';
    }

    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  const isValidUrl = (string) => {
    try {
      new URL(string);
      return true;
    } catch (_) {
      return false;
    }
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    if (!validateForm()) {
      return;
    }

    const savedAlbums = JSON.parse(localStorage.getItem('albumsData') || '[]');

    // Find the album and add the photo
    const updatedAlbums = savedAlbums.map(album => {
      if (album.id === formData.albumId) {
        const newPhoto = {
          id: Date.now(), // Simple ID generation
          src: formData.src,
          title: formData.title || '',
          description: formData.description || ''
        };

        return {
          ...album,
          photos: [...(album.photos || []), newPhoto],
          photoCount: (album.photoCount || 0) + 1
        };
      }
      return album;
    });

    localStorage.setItem('albumsData', JSON.stringify(updatedAlbums));
    navigate('/admin/dashboard');
  };

  return (
    <div className="photo-form-page">
      <div className="admin-header">
        <div className="container">
          <div className="header-content">
            <h1>Ajouter une photo</h1>
            <button 
              onClick={() => navigate('/admin/dashboard')} 
              className="back-btn"
            >
              Retour
            </button>
          </div>
        </div>
      </div>

      <div className="form-content">
        <div className="container">
          <div className="form-container">
            <form onSubmit={handleSubmit} className="photo-form">
              <div className="form-group">
                <label htmlFor="albumId">Album de destination *</label>
                <select
                  id="albumId"
                  name="albumId"
                  value={formData.albumId}
                  onChange={handleChange}
                  className={errors.albumId ? 'error' : ''}
                >
                  <option value="">SÃ©lectionnez un album</option>
                  {albums.map(album => (
                    <option key={album.id} value={album.id}>
                      {album.title} ({album.photoCount || 0} photos)
                    </option>
                  ))}
                </select>
                {errors.albumId && <span className="error-text">{errors.albumId}</span>}
              </div>

              <div className="form-group">
                <label htmlFor="src">URL de la photo *</label>
                <input
                  type="url"
                  id="src"
                  name="src"
                  value={formData.src}
                  onChange={handleChange}
                  className={errors.src ? 'error' : ''}
                  placeholder="https://example.com/photo.jpg"
                />
                {errors.src && <span className="error-text">{errors.src}</span>}
                {formData.src && isValidUrl(formData.src) && (
                  <div className="image-preview">
                    <img src={formData.src} alt="AperÃ§u" />
                  </div>
                )}
              </div>

              <div className="form-group">
                <label htmlFor="title">Titre de la photo (optionnel)</label>
                <input
                  type="text"
                  id="title"
                  name="title"
                  value={formData.title}
                  onChange={handleChange}
                  placeholder="Titre de la photo"
                />
                <small>Laissez vide si vous ne souhaitez pas afficher de titre</small>
              </div>

              <div className="form-group">
                <label htmlFor="description">Description (optionnelle)</label>
                <textarea
                  id="description"
                  name="description"
                  value={formData.description}
                  onChange={handleChange}
                  placeholder="Description de la photo"
                  rows="3"
                />
                <small>Laissez vide si vous ne souhaitez pas afficher de description</small>
              </div>

              <div className="form-actions">
                <button 
                  type="button" 
                  onClick={() => navigate('/admin/dashboard')}
                  className="cancel-btn"
                >
                  Annuler
                </button>
                <button type="submit" className="submit-btn">
                  Ajouter la photo
                </button>
              </div>
            </form>

            {albums.length === 0 && (
              <div className="no-albums-message">
                <p>Aucun album disponible. Vous devez d'abord crÃ©er un album avant d'ajouter des photos.</p>
                <button 
                  onClick={() => navigate('/admin/albums/new')}
                  className="create-album-btn"
                >
                  CrÃ©er un album
                </button>
              </div>
            )}
          </div>
        </div>
      </div>
    </div>
  );
};

export default PhotoForm;
