import React, { useState } from 'react';
import emailjs from '@emailjs/browser';
import { EMAILJS_CONFIG } from '../config/emailjs';
import './Contact.css';

const Contact = () => {
  const [formData, setFormData] = useState({
    nom: '',
    prenom: '',
    email: '',
    message: ''
  });
  const [isSubmitting, setIsSubmitting] = useState(false);
  const [submitMessage, setSubmitMessage] = useState('');

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setIsSubmitting(true);
    setSubmitMessage('');

    try {
      // Vérification de la configuration EmailJS
      if (EMAILJS_CONFIG.PUBLIC_KEY === 'YOUR_EMAILJS_PUBLIC_KEY') {
        // Mode développement - simulation
        console.log('Mode développement - EmailJS non configuré');
        await new Promise(resolve => setTimeout(resolve, 1000));
        setSubmitMessage('⚠️ Mode développement : EmailJS n\'est pas encore configuré. Consultez le fichier config/emailjs.js pour la configuration.');
        setFormData({ nom: '', prenom: '', email: '', message: '' });
        return;
      }

      // Préparation des paramètres pour EmailJS
      const templateParams = {
        from_name: `${formData.prenom} ${formData.nom}`,
        from_email: formData.email,
        message: formData.message,
        to_email: 'thomas.pretre.pro@gmail.com',
        reply_to: formData.email
      };

      // Envoi via EmailJS
      const response = await emailjs.send(
        EMAILJS_CONFIG.SERVICE_ID,
        EMAILJS_CONFIG.TEMPLATE_ID,
        templateParams,
        EMAILJS_CONFIG.PUBLIC_KEY
      );

      console.log('Email envoyé avec succès:', response);
      setSubmitMessage('✅ Votre message a été envoyé avec succès ! Je vous répondrai dans les plus brefs délais.');
      setFormData({ nom: '', prenom: '', email: '', message: '' });

    } catch (error) {
      console.error('Erreur lors de l\'envoi:', error);

      // Messages d'erreur spécifiques
      let errorMessage = 'Une erreur est survenue lors de l\'envoi. Veuillez réessayer.';

      if (error.text) {
        if (error.text.includes('Invalid')) {
          errorMessage = 'Configuration EmailJS invalide. Vérifiez vos clés dans config/emailjs.js';
        } else if (error.text.includes('rate limit')) {
          errorMessage = 'Trop de tentatives. Veuillez attendre quelques minutes avant de réessayer.';
        }
      }

      setSubmitMessage(`❌ ${errorMessage}`);
    } finally {
      setIsSubmitting(false);
    }
  };

  return (
    <div className="contact">
      <div className="container">
        <div className="contact-header">
          <h1>Contact</h1>
          <p>Parlons de votre projet photo</p>
        </div>

        <div className="contact-content">
          <div className="contact-info">
            <h2>Restons en contact</h2>
            <p>
              Vous avez un projet photo en tête ? Une séance portrait, un événement à immortaliser, 
              ou simplement envie de discuter photographie ? N'hésitez pas à me contacter !
            </p>

            <div className="contact-details">
              <div className="contact-item">
                <h3>Email</h3>
                <p>thomas.pretre.pro@gmail.com</p>
              </div>

              <div className="contact-item">
                <h3>Spécialités</h3>
                <ul>
                  <li>Portraits professionnels</li>
                  <li>Photographie d'événements</li>
                  <li>Paysages et nature</li>
                  <li>Séances photo personnalisées</li>
                </ul>
              </div>

              <div className="contact-item">
                <h3>Temps de réponse</h3>
                <p>Je réponds généralement sous 24h</p>
              </div>
            </div>
          </div>

          <div className="contact-form-container">
            <form className="contact-form" onSubmit={handleSubmit}>
              <div className="form-row">
                <div className="form-group">
                  <label htmlFor="prenom">Prénom *</label>
                  <input
                    type="text"
                    id="prenom"
                    name="prenom"
                    value={formData.prenom}
                    onChange={handleChange}
                    required
                  />
                </div>
                <div className="form-group">
                  <label htmlFor="nom">Nom *</label>
                  <input
                    type="text"
                    id="nom"
                    name="nom"
                    value={formData.nom}
                    onChange={handleChange}
                    required
                  />
                </div>
              </div>

              <div className="form-group">
                <label htmlFor="email">Email *</label>
                <input
                  type="email"
                  id="email"
                  name="email"
                  value={formData.email}
                  onChange={handleChange}
                  required
                />
              </div>

              <div className="form-group">
                <label htmlFor="message">Message *</label>
                <textarea
                  id="message"
                  name="message"
                  rows="6"
                  value={formData.message}
                  onChange={handleChange}
                  placeholder="Décrivez votre projet, vos besoins, ou posez-moi vos questions..."
                  required
                ></textarea>
              </div>

              <button 
                type="submit" 
                className="submit-btn"
                disabled={isSubmitting}
              >
                {isSubmitting ? 'Envoi en cours...' : 'Envoyer le message'}
              </button>

              {submitMessage && (
                <div className={`submit-message ${
                  submitMessage.includes('✅') ? 'success' : 
                  submitMessage.includes('⚠️') ? 'warning' : 'error'
                }`}>
                  {submitMessage}
                </div>
              )}
            </form>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Contact;
